INSERT INTO `theloai` VALUES (1,'Ngôn tình',NULL,'2023-05-10 22:35:03'),(2,'Đam Mỹ',NULL,NULL),(3,'Đô thị',NULL,NULL),(4,'Xuyên không','2023-05-10 22:02:37','2023-05-10 22:35:38'),(5,'Nữ phụ','2023-05-11 18:39:54','2023-05-11 18:39:54'),(6,'Hài hước','2023-05-11 18:40:00','2023-05-11 18:40:05'),(7,'Trinh thám','2023-05-11 18:58:43','2023-05-11 18:58:43'),(8,'Huyền huyễn','2023-05-11 19:14:05','2023-05-11 19:14:05');
